import React from "react";
import Note from "./Note";
import AddNote from "./AddNote";

/* this is component storing the notes in a format */
const NoteList = ({ notes, handleAddNote, handleDelete }) => {
  return (
    <div className="notes-list">
      {/*here we are going to use .map to make each note card from notes
        and also passing the functions and other states as propas*/}
      {notes.map((note) => (
        <Note
          id={note.id}
          text={note.text}
          date={note.date}
          handleDelete={handleDelete}
        />
      ))}
      <AddNote handleAddNote={handleAddNote}/>
    </div>
  );
};

export default NoteList;
